define('ROOT_DIR', realpath(dirname(__FILE__)) .'/');
define('CONTENT_DIR', ROOT_DIR .'content/');

// Functions include
include('php/_functions.php');

// Options
$script_location = "##JSLOCATION##";
$file_format = ".md"; // Markdown
$available_branches = "##AVAILABLE_BRANCHES##"; // What branches (languages) do we actually have on GitHub?
$working_files = array("index","readme","about"); // What files are the 'working in environment' ones?

// Working with URI and detecting locale (multilanguage)
$url = '';
$request_url = (isset($_SERVER['REQUEST_URI'])) ? $_SERVER['REQUEST_URI'] : '';
$script_url  = (isset($_SERVER['PHP_SELF'])) ? $_SERVER['PHP_SELF'] : '';
$full_url = $request_url.$script_url;
$locale = detect_locale();

// Get currently viewed locale
$locs = explode(',',$available_branches);
foreach ($locs as $loc) {
	if (strpos($full_url,'/'.strtolower($loc).'/') !== false or strpos($full_url,'/'.$loc.'/') !== false) {
		$viewed_locale = strtolower($loc);
	}
}

// Get our url path and trim the / of the left and the right + working with locales
if($request_url != $script_url) {
	$url = trim(preg_replace('/'.str_replace('/', '\/', str_replace('index.php', '', $script_url)) .'/', '', $request_url, 1), '/');
}
if ($request_url == '/') {
	header("Location: /".$locale,TRUE,303); // With current locale detected, we are sending a 303 header which tells crawler where to go and redirects user to their language zone
}

// Setting up original link to this content, stored on GitHub - just for comparing cases and ofc for coders, who want to modify particular file
if ($url != $locale) {
	$git_origin = str_replace($viewed_locale.'/',strtoupper($viewed_locale.'/'),$url);
	$git_origin = $git_origin.$file_format;
} else {
	$git_origin = strtoupper($url);
}

// Transforming some filenames to uppercase (README, ABOUT, INDEX)
foreach ($working_files as $wfile) {
	$pos = strpos($git_origin, '/'.$wfile);
	if ($pos !== false) {
		$git_origin = str_replace('/'.$wfile,strtoupper('/'.$wfile),$git_origin);
	}
}

// Get the file path
if($url) $file = strtolower(CONTENT_DIR.$url);
else $file = CONTENT_DIR.'index';

// Load the file
if(is_dir($file)) $file = CONTENT_DIR.$url.'/index'.$file_format;
else $file .= $file_format;

// Show 404 if file cannot be found
if(file_exists($file)) {
	$content = file_get_contents($file);
} else {
	if (!file_exists(CONTENT_DIR.$viewed_locale.'/404'.$file_format)) $locale = 'en'; // Defaults 404 error to EN if no user locale found
	if (!$viewed_locale) $viewed_locale = $locale;
	$content = file_get_contents(CONTENT_DIR.$viewed_locale.'/404'.$file_format);
	$git_origin = '';
}
